function Options({ options, dispatch, answer }) {
  return (
    <div className="options">
      {options.map((option, idx) => {
        return (
          <button
            className="btn btn-option"
            onClick={() => dispatch({ type: "newAnswer", payload: answer })}
          >
            {option}
          </button>
        );
      })}
    </div>
  );
}

export default Options;
